import React, { Component } from 'react'
import PropTypes from 'prop-types'
import NewsItem from './NewsItem'

export class NewsCom extends Component {
    // constructor initialize the object(state)
    
  constructor(){
    super();
    this.state={
        // t
            articles:[],
            loading: false

    }
  }
//  IMP LINE -->> for fetch the news
  async componentDidMount(){
    let url_var="https://newsapi.org/v2/top-headlines?country=in&apiKey=f44b5f678b434be4a63fc61f564befe5";
    let news_array=await fetch(url_var);
    let news_arr_json=await news_array.json()
    this.setState({articles:news_arr_json.articles})
  }

  render() {
    return (
      <div>
        
        <div className="container">
            <h2 className='my-5' style={{textAlign:"center"}}>------  NewsFlash -- Top Headlines  ------</h2>
                <div className="row">
                    {/* t */}
                {this.state.articles.map((element1)=>{
                        return  <div className="col-md-4 my-2" key={element1.url}>
                             <NewsItem title={element1.title.slice(0,44)} desc={element1.description?element1.description.slice(0,88):""} NewsUrl={element1.url} 
                ImageUrl={element1.urlToImage}/>
                 {/* <NewsItem title={element1.title.slice(0,44)} desc={element1.description.slice(0,100)} NewsUrl={element1.url} 
                ImageUrl={element1.urlToImage}/> */}
                         </div>
            })}
            </div>
           
        </div>
      </div>
    )
  }
}

export default NewsCom
