import React, { Component } from 'react'
import PropTypes from 'prop-types'

export class NewsItem extends Component {

  render() {
    let {title,desc,ImageUrl,NewsUrl,author,timing,source}=this.props;
    return (
      <div>
        {/* style object there */}
        <div className="card" style={{width: "18rem"}}>
        <span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
        {source}
        </span>
            <img src={!ImageUrl?"https://earthsky.org/upl/2023/01/Venus-Saturn-jan21-2023-John-Ashley-Sonoita-AZ-e1674391826539.jpg":ImageUrl} 
            className="card-img-top" alt=".."/>
            <div className="card-body">
                <h5 className="card-title">{title}....</h5>
                <p className="card-text">{desc}....</p>
                {/* author== null likho ya (!author) sirf(author) likhoga to bo nul nhi mana jayga */}
                <p class="card-text"><small class="text-muted"> By {!author?"Unknown":author} on {new Date(timing).toGMTString()}</small></p>
                <a href={NewsUrl} target="_blank" className="btn btn-sm btn-primary"> <b>Read More</b> </a>
            </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
