import React, { Component } from 'react'
import PropTypes from 'prop-types'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export class NewsCom extends Component {
    // constructor initialize the object(state)
    
  constructor(props){
    super(props);
    this.state={
            articles:[],
            loading: false,
            page:1
    }
    document.title=`${this.props.category} -- NewsFlash`
  }
  capalizeLetter=(title_str)=>{

  }

 
//  IMP LINE -->> for fetch the news
//  IMP LINE -->>  this componentDidMount() function will run in syscronous form(line by line) and will stop on await 
// unit it does not complete
  async componentDidMount(){
    // direct link sa fetch
    let url_var=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=f44b5f678b434be4a63fc61f564befe5&page=1&pagesize=${this.props.pageSize}`;
    // IMP LINE -->>> the "pagesize" parameter is used to provide the size of page and "page" parameter is used to define 
    // the page number and api also has its defalut "pagesize"

    let news_array=await fetch(url_var);
    let news_arr_json=await news_array.json()
    this.setState({articles:news_arr_json.articles,totalResults:news_arr_json.totalResults})
    // update sa totalResults bhi aa gya

    // json file ma articles ka andar data array hoga to articles tak pahuch jao
  }
  clickNext=async()=>{
    // IMP LINE -->>>props js part ma bina kisi const ka this.props sa access honga par jsx ma direct {props_name}
    if(this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize)){
    }
    else{

      let url_var=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=f44b5f678b434be4a63fc61f564befe5&page=${this.state.page +1}&pagesize=${this.props.pageSize}`;
      this.setState({
        loading:true
      })
      this.setState({page:this.state.page +1});
      let news_array=await fetch(url_var);
      let news_arr_json=await news_array.json()
      this.setState({articles:news_arr_json.articles,loading:false})
    }
}

 
  clickPrivious=async()=>{
    let url_var=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=f44b5f678b434be4a63fc61f564befe5&page=${this.state.page - 1}&pagesize=${this.props.pageSize}`;
    this.setState({
      loading:true
    })
    this.setState({page:this.state.page -1});
    let news_array=await fetch(url_var);
    let news_arr_json=await news_array.json()
    this.setState({articles:news_arr_json.articles,loading:false})

}

  render() {
    
    return (
      
      <div>
        {/* color:"white",padding:"15px",borderRadius:"3rem" */}
        <div className="container">
            <h3 className='my-5' style={{textAlign:"center",fontWeight:"600",borderBottom:"2px solid black",paddingBottom:"20px"}}>  NewsFlash -- Top Headlines </h3>
            {/* IMP LINE -->> kisi tag ko use nhi karna ho agar disable possible na ho to ya use kar sakta ha 
            both true or action ma hona chahiya tabhi run hoga  */}
            {this.state.loading && <Spinner/>}
                <div className="row">
                    {/* false(loading nhi par) par news show hongi */}
              {!this.state.loading && this.state.articles.map((element1)=>{
                        return  <div className="col-md-4 my-2" key={element1.url}>
                            {/*IMP LINE --->>> there value is null then we can set the default value by turnary condition from main fetch position */}
                             <NewsItem title={element1.title.slice(0,44)} desc={element1.description?element1.description.slice(0,88):"There are not have any description you can click on read more and can read full news"} NewsUrl={element1.url} 
                ImageUrl={element1.urlToImage} author={element1.author} timing={element1.publishedAt} source={element1.source.name}/>
                 {/* <NewsItem title={element1.title.slice(0,44)} desc={element1.description.slice(0,100)} NewsUrl={element1.url} 
                ImageUrl={element1.urlToImage}/> */}
                         </div>
            })}
            </div>
           <div className="container my-5 d-flex justify-content-between">
              <button disabled={this.state.page===1} type="button" className="btn btn-dark" onClick={this.clickPrivious}> <b> &#8592; Privious</b> </button>
              <button disabled={this.state.page +1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.clickNext}> <b>Next &#8594; </b> </button>
           </div>
        </div>
      </div>
    )
  }
}

export default NewsCom
